import React, { useEffect, useState } from "react";
import api from "../services/api";
import { useNavigate } from "react-router-dom";
import TaskList from "../components/TaskList";
import TaskForm from "../components/TaskForm";
import logo from "../assets/logo.png"; // Importa il logo

const Dashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [userName, setUserName] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    fetchTasks();
    fetchUserName();
  }, []);

  const fetchTasks = async () => {
    try {
      const res = await api.get("/tasks");
      setTasks(res.data);
    } catch (error) {
      alert("❌ Errore nel recupero delle attività");
    }
  };

  const fetchUserName = async () => {
    try {
      const res = await api.get("/auth/user");
      setUserName(res.data.name);
    } catch (error) {
      console.error("Errore nel recupero del nome utente:", error);
    }
  };

  const addTask = async (name) => {
    await api.post("/tasks", { name });
    fetchTasks();
  };

  const deleteTask = async (id) => {
    await api.delete(`/tasks/${id}`);
    fetchTasks();
  };

  const toggleTask = (id) => {
    setTasks(tasks.map(task => task.id === id ? { ...task, completed: !task.completed } : task));
  };

  const logout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const confirmDeleteAccount = () => {
    navigate("/confirm-delete-account");
  };

  return (
    <div className="dashboard-container">
      <div className="top-bar">
        <img src={logo} alt="Logo" className="logo" /> {/* Aggiungi il logo */}
        <div className="greeting">
          <i className="fas fa-user"></i> {/* Aggiungi l'icona dello user */}
          Ciao, {userName}
        </div>
        <div className="button-container">
          <button className="logout-button" onClick={logout}>Logout</button>
          <button className="delete-account-button" onClick={confirmDeleteAccount}>Elimina Account</button>
        </div>
      </div>
      <h2>Dashboard</h2>
      <TaskForm onAdd={addTask} />
      <TaskList tasks={tasks} onDelete={deleteTask} onToggle={toggleTask} />
    </div>
  );
};

export default Dashboard;
