import React, { useState, useEffect } from "react";
import api from "../services/api";
import { useNavigate, Link } from "react-router-dom";

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nameAvailable, setNameAvailable] = useState(true);
  const [passwordError, setPasswordError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "Registrazione - Todo List";
  }, []);

  const checkNameAvailability = async (name) => {
    try {
      const res = await api.post("/auth/check-name", { name });
      setNameAvailable(res.data.available);
    } catch (error) {
      console.error("Errore nel controllo del nome:", error);
    }
  };

  const validatePassword = (password) => {
    const regex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    return regex.test(password);
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    if (!validatePassword(password)) {
      setPasswordError("La password deve contenere almeno 8 caratteri, una lettera maiuscola, almeno una minuscola, un numero e un simbolo speciale.");
      return;
    }
    try {
      await api.post("/auth/register", { name, email, password });
      alert("Registrazione completata! Ora puoi accedere.");
      navigate("/login");
    } catch (error) {
      alert("Errore di registrazione: " + error.response.data.message);
    }
  };

  useEffect(() => {
    if (name) {
      checkNameAvailability(name);
    }
  }, [name]);

  return (
    <div className="form-container">
      <h2>Registrati</h2>
      <form onSubmit={handleRegister}>
        <input
          type="text"
          placeholder="Nome"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        {!nameAvailable && <p className="error">Nome non disponibile</p>}
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        {passwordError && <p className="error">{passwordError}</p>}
        <button type="submit">Registrati</button>
      </form>
      <p>
        Hai gi√† un account? <Link to="/login">Accedi</Link>
      </p>
    </div>
  );
};

export default Register;
